import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useMutation, useQuery, useQueryClient } from 'react-query';
import { studentService, courseService } from '../services/api';
import LoadingSpinner from './LoadingSpinner';
import {
  UserIcon,
  AcademicCapIcon,
  DocumentTextIcon,
  CalendarIcon,
  PhoneIcon,
  EnvelopeIcon,
  MapPinIcon,
  UserGroupIcon,
  HeartIcon,
  ExclamationTriangleIcon
} from '@heroicons/react/24/outline';

const StudentForm = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const isEditing = Boolean(id);
  const queryClient = useQueryClient();

  // Estado del formulario
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    documentNumber: '',
    documentType: 'tarjeta_identidad',
    grade: '',
    section: '',
    birthDate: '',
    gender: 'masculino',
    phone: '',
    email: '',
    address: '',
    parentName: '',
    parentPhone: '',
    parentEmail: '',
    emergencyContact: '',
    medicalInfo: '',
    courseId: '',
    notes: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Obtener cursos disponibles
  const { data: coursesData } = useQuery('courses', () => courseService.getCourses());

  // Obtener datos del estudiante si estamos editando
  const { data: studentData, isLoading: loadingStudent } = useQuery(
    ['student', id],
    () => studentService.getStudent(id),
    {
      enabled: isEditing,
      onSuccess: (data) => {
        if (data?.data) {
          const student = data.data;
          setFormData({
            firstName: student.firstName || '',
            lastName: student.lastName || '',
            documentNumber: student.documentNumber || '',
            documentType: student.documentType || 'tarjeta_identidad',
            grade: student.grade || '',
            section: student.section || '',
            birthDate: student.birthDate ? student.birthDate.split('T')[0] : '',
            gender: student.gender || 'masculino',
            phone: student.phone || '',
            email: student.email || '',
            address: student.address || '',
            parentName: student.parentName || '',
            parentPhone: student.parentPhone || '',
            parentEmail: student.parentEmail || '',
            emergencyContact: student.emergencyContact || '',
            medicalInfo: student.medicalInfo || '',
            courseId: student.courseId || '',
            notes: student.notes || ''
          });
        }
      }
    }
  );

  // Mutación para crear/actualizar estudiante
  const studentMutation = useMutation(
    (data) => isEditing ? studentService.updateStudent(id, data) : studentService.createStudent(data),
    {
      onSuccess: (data) => {
        queryClient.invalidateQueries('students');
        queryClient.invalidateQueries(['student', id]);
        
        // Navegar a la vista de detalle del estudiante si es creación exitosa
        if (!isEditing && data?.data?.id) {
          navigate(`/gestion-estudiantes/${data.data.id}`);
        } else if (isEditing) {
          navigate(`/gestion-estudiantes/${id}`);
        } else {
          navigate('/gestion-estudiantes');
        }
      },
      onError: (error) => {
        if (error.response?.data?.errors) {
          const backendErrors = {};
          error.response.data.errors.forEach(err => {
            backendErrors[err.param || err.path] = err.msg;
          });
          setErrors(backendErrors);
        } else {
          setErrors({ general: error.response?.data?.error || 'Error al guardar el estudiante' });
        }
      }
    }
  );

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Limpiar errores del campo
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    // Validaciones requeridas
    if (!formData.firstName.trim()) newErrors.firstName = 'El nombre es requerido';
    if (!formData.lastName.trim()) newErrors.lastName = 'El apellido es requerido';
    if (!formData.documentNumber.trim()) newErrors.documentNumber = 'El número de documento es requerido';
    if (!formData.grade.trim()) newErrors.grade = 'El grado es requerido';
    if (!formData.section.trim()) newErrors.section = 'La sección es requerida';
    if (!formData.birthDate) newErrors.birthDate = 'La fecha de nacimiento es requerida';
    if (!formData.parentName.trim()) newErrors.parentName = 'El nombre del padre/madre/acudiente es requerido';
    if (!formData.parentPhone.trim()) newErrors.parentPhone = 'El teléfono del padre/madre/acudiente es requerido';

    // Validaciones de formato
    if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Email inválido';
    }
    if (formData.parentEmail && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.parentEmail)) {
      newErrors.parentEmail = 'Email del padre/madre/acudiente inválido';
    }

    // Validación de edad
    if (formData.birthDate) {
      const today = new Date();
      const birthDate = new Date(formData.birthDate);
      const age = today.getFullYear() - birthDate.getFullYear();
      if (age < 3 || age > 25) {
        newErrors.birthDate = 'La edad debe estar entre 3 y 25 años';
      }
    }

    // Validación de curso para secundaria
    if (formData.grade && ['1°', '2°', '3°', '4°', '5°'].includes(formData.grade) && !formData.courseId) {
      newErrors.courseId = 'El curso es requerido para estudiantes de secundaria';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);
    
    try {
      await studentMutation.mutateAsync(formData);
    } catch (error) {
      // El error ya se maneja en la mutación
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isEditing && loadingStudent) {
    return (
      <div className="flex justify-center items-center h-64">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  const courses = coursesData?.data || [];
  const grades = ['Pre-K', 'Kinder', '1°', '2°', '3°', '4°', '5°', '6°'];
  const sections = ['A', 'B', 'C', 'D', 'E'];

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Encabezado */}
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">
              {isEditing ? 'Editar Estudiante' : 'Nuevo Estudiante'}
            </h1>
            <p className="text-gray-600 mt-1">
              {isEditing ? 'Actualiza la información del estudiante' : 'Registra un nuevo estudiante en el sistema'}
            </p>
          </div>
          <div className="flex space-x-3">
            <button
              type="button"
              onClick={() => navigate('/estudiantes')}
              className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Cancelar
            </button>
          </div>
        </div>
      </div>

      {/* Formulario */}
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Error general */}
        {errors.general && (
          <div className="bg-red-50 border border-red-200 rounded-md p-4">
            <div className="flex">
              <ExclamationTriangleIcon className="h-5 w-5 text-red-400" />
              <div className="ml-3">
                <h3 className="text-sm font-medium text-red-800">Error</h3>
                <div className="mt-2 text-sm text-red-700">
                  {errors.general}
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Información Personal */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex items-center mb-4">
            <UserIcon className="h-5 w-5 text-blue-500 mr-2" />
            <h3 className="text-lg font-medium text-gray-900">Información Personal</h3>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <div>
              <label htmlFor="firstName" className="block text-sm font-medium text-gray-700 mb-1">
                Nombre *
              </label>
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.firstName ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="Nombres del estudiante"
              />
              {errors.firstName && <p className="mt-1 text-sm text-red-600">{errors.firstName}</p>}
            </div>

            <div>
              <label htmlFor="lastName" className="block text-sm font-medium text-gray-700 mb-1">
                Apellido *
              </label>
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.lastName ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="Apellidos del estudiante"
              />
              {errors.lastName && <p className="mt-1 text-sm text-red-600">{errors.lastName}</p>}
            </div>

            <div>
              <label htmlFor="documentType" className="block text-sm font-medium text-gray-700 mb-1">
                Tipo de Documento
              </label>
              <select
                id="documentType"
                name="documentType"
                value={formData.documentType}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="tarjeta_identidad">Tarjeta de Identidad</option>
                <option value="cedula">Cédula</option>
                <option value="pasaporte">Pasaporte</option>
              </select>
            </div>

            <div>
              <label htmlFor="documentNumber" className="block text-sm font-medium text-gray-700 mb-1">
                Número de Documento *
              </label>
              <input
                type="text"
                id="documentNumber"
                name="documentNumber"
                value={formData.documentNumber}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.documentNumber ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="Número de documento"
              />
              {errors.documentNumber && <p className="mt-1 text-sm text-red-600">{errors.documentNumber}</p>}
            </div>

            <div>
              <label htmlFor="birthDate" className="block text-sm font-medium text-gray-700 mb-1">
                Fecha de Nacimiento *
              </label>
              <input
                type="date"
                id="birthDate"
                name="birthDate"
                value={formData.birthDate}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.birthDate ? 'border-red-300' : 'border-gray-300'
                }`}
              />
              {errors.birthDate && <p className="mt-1 text-sm text-red-600">{errors.birthDate}</p>}
            </div>

            <div>
              <label htmlFor="gender" className="block text-sm font-medium text-gray-700 mb-1">
                Género
              </label>
              <select
                id="gender"
                name="gender"
                value={formData.gender}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="masculino">Masculino</option>
                <option value="femenino">Femenino</option>
                <option value="otro">Otro</option>
              </select>
            </div>
          </div>
        </div>

        {/* Información Académica */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex items-center mb-4">
            <AcademicCapIcon className="h-5 w-5 text-green-500 mr-2" />
            <h3 className="text-lg font-medium text-gray-900">Información Académica</h3>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <div>
              <label htmlFor="grade" className="block text-sm font-medium text-gray-700 mb-1">
                Grado *
              </label>
              <select
                id="grade"
                name="grade"
                value={formData.grade}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.grade ? 'border-red-300' : 'border-gray-300'
                }`}
              >
                <option value="">Seleccionar grado</option>
                {grades.map(grade => (
                  <option key={grade} value={grade}>{grade}</option>
                ))}
              </select>
              {errors.grade && <p className="mt-1 text-sm text-red-600">{errors.grade}</p>}
            </div>

            <div>
              <label htmlFor="section" className="block text-sm font-medium text-gray-700 mb-1">
                Sección *
              </label>
              <select
                id="section"
                name="section"
                value={formData.section}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.section ? 'border-red-300' : 'border-gray-300'
                }`}
              >
                <option value="">Seleccionar sección</option>
                {sections.map(section => (
                  <option key={section} value={section}>{section}</option>
                ))}
              </select>
              {errors.section && <p className="mt-1 text-sm text-red-600">{errors.section}</p>}
            </div>

            {/* Curso (solo para secundaria) */}
            {formData.grade && ['1°', '2°', '3°', '4°', '5°'].includes(formData.grade) && (
              <div>
                <label htmlFor="courseId" className="block text-sm font-medium text-gray-700 mb-1">
                  Curso (Secundaria) *
                </label>
                <select
                  id="courseId"
                  name="courseId"
                  value={formData.courseId}
                  onChange={handleInputChange}
                  className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                    errors.courseId ? 'border-red-300' : 'border-gray-300'
                  }`}
                >
                  <option value="">Seleccionar curso</option>
                  {courses
                    .filter(course => course.level === 'secundaria')
                    .map(course => (
                      <option key={course.id} value={course.id}>
                        {course.name} - {course.academicYear}
                      </option>
                    ))}
                </select>
                {errors.courseId && <p className="mt-1 text-sm text-red-600">{errors.courseId}</p>}
              </div>
            )}
          </div>
        </div>

        {/* Información de Contacto */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex items-center mb-4">
            <PhoneIcon className="h-5 w-5 text-purple-500 mr-2" />
            <h3 className="text-lg font-medium text-gray-900">Información de Contacto</h3>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label htmlFor="phone" className="block text-sm font-medium text-gray-700 mb-1">
                Teléfono del Estudiante
              </label>
              <input
                type="tel"
                id="phone"
                name="phone"
                value={formData.phone}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Teléfono del estudiante"
              />
            </div>

            <div>
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                Email del Estudiante
              </label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.email ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="email@ejemplo.com"
              />
              {errors.email && <p className="mt-1 text-sm text-red-600">{errors.email}</p>}
            </div>

            <div className="md:col-span-2">
              <label htmlFor="address" className="block text-sm font-medium text-gray-700 mb-1">
                Dirección
              </label>
              <textarea
                id="address"
                name="address"
                rows={3}
                value={formData.address}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Dirección de residencia del estudiante"
              />
            </div>
          </div>
        </div>

        {/* Información del Padre/Madre/Acudiente */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex items-center mb-4">
            <UserGroupIcon className="h-5 w-5 text-orange-500 mr-2" />
            <h3 className="text-lg font-medium text-gray-900">Padre/Madre/Acudiente</h3>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label htmlFor="parentName" className="block text-sm font-medium text-gray-700 mb-1">
                Nombre Completo *
              </label>
              <input
                type="text"
                id="parentName"
                name="parentName"
                value={formData.parentName}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.parentName ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="Nombre del padre/madre/acudiente"
              />
              {errors.parentName && <p className="mt-1 text-sm text-red-600">{errors.parentName}</p>}
            </div>

            <div>
              <label htmlFor="parentPhone" className="block text-sm font-medium text-gray-700 mb-1">
                Teléfono *
              </label>
              <input
                type="tel"
                id="parentPhone"
                name="parentPhone"
                value={formData.parentPhone}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.parentPhone ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="Teléfono del padre/madre/acudiente"
              />
              {errors.parentPhone && <p className="mt-1 text-sm text-red-600">{errors.parentPhone}</p>}
            </div>

            <div>
              <label htmlFor="parentEmail" className="block text-sm font-medium text-gray-700 mb-1">
                Email del Padre/Madre/Acudiente
              </label>
              <input
                type="email"
                id="parentEmail"
                name="parentEmail"
                value={formData.parentEmail}
                onChange={handleInputChange}
                className={`block w-full px-3 py-2 border rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 ${
                  errors.parentEmail ? 'border-red-300' : 'border-gray-300'
                }`}
                placeholder="email@ejemplo.com"
              />
              {errors.parentEmail && <p className="mt-1 text-sm text-red-600">{errors.parentEmail}</p>}
            </div>

            <div>
              <label htmlFor="emergencyContact" className="block text-sm font-medium text-gray-700 mb-1">
                Contacto de Emergencia
              </label>
              <input
                type="text"
                id="emergencyContact"
                name="emergencyContact"
                value={formData.emergencyContact}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Nombre y teléfono de contacto de emergencia"
              />
            </div>
          </div>
        </div>

        {/* Información Adicional */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex items-center mb-4">
            <HeartIcon className="h-5 w-5 text-red-500 mr-2" />
            <h3 className="text-lg font-medium text-gray-900">Información Adicional</h3>
          </div>
          
          <div className="space-y-6">
            <div>
              <label htmlFor="medicalInfo" className="block text-sm font-medium text-gray-700 mb-1">
                Información Médica
              </label>
              <textarea
                id="medicalInfo"
                name="medicalInfo"
                rows={3}
                value={formData.medicalInfo}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Alergias, medicamentos, condiciones médicas relevantes, etc."
              />
            </div>

            <div>
              <label htmlFor="notes" className="block text-sm font-medium text-gray-700 mb-1">
                Notas Adicionales
              </label>
              <textarea
                id="notes"
                name="notes"
                rows={3}
                value={formData.notes}
                onChange={handleInputChange}
                className="block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                placeholder="Observaciones adicionales sobre el estudiante"
              />
            </div>
          </div>
        </div>

        {/* Botones de acción */}
        <div className="bg-white shadow rounded-lg p-6">
          <div className="flex justify-end space-x-3">
            <button
              type="button"
              onClick={() => navigate('/estudiantes')}
              className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Cancelar
            </button>
            <button
              type="submit"
              disabled={isSubmitting || studentMutation.isLoading}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSubmitting || studentMutation.isLoading ? (
                <>
                  <LoadingSpinner size="sm" className="mr-2" />
                  {isEditing ? 'Actualizando...' : 'Guardando...'}
                </>
              ) : (
                isEditing ? 'Actualizar Estudiante' : 'Crear Estudiante'
              )}
            </button>
          </div>
        </div>
      </form>
    </div>
  );
};

export default StudentForm;
