import React, { createContext, useContext, useReducer, useEffect } from 'react';
import { authService } from '../services/api';

// Estados de autenticaciÃ³n
const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return {
        ...state,
        loading: true,
        error: null
      };
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        loading: false,
        isAuthenticated: true,
        user: action.payload.user,
        token: action.payload.token,
        error: null
      };
    case 'LOGIN_FAILURE':
      return {
        ...state,
        loading: false,
        isAuthenticated: false,
        user: null,
        token: null,
        error: action.payload
      };
    case 'LOGOUT':
      return {
        ...state,
        loading: false,
        isAuthenticated: false,
        user: null,
        token: null,
        error: null
      };
    case 'UPDATE_USER':
      return {
        ...state,
        user: action.payload
      };
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    default:
      return state;
  }
};

// Estado inicial
const initialState = {
  isAuthenticated: false,
  user: null,
  token: null,
  loading: true,
  error: null
};

// Crear contexto
const AuthContext = createContext();

// Provider del contexto
export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  // Verificar si hay token al cargar la aplicaciÃ³n
  useEffect(() => {
    const initializeAuth = async () => {
      console.log('ðŸ”„ Inicializando autenticaciÃ³n...');
      const token = localStorage.getItem('authToken');
      const userStr = localStorage.getItem('user');

      if (token && userStr) {
        try {
          const user = JSON.parse(userStr);
          console.log('âœ… Token y usuario encontrados en localStorage:', user.email);
          
          // Por ahora, confiamos en el localStorage sin verificar con el servidor
          // TODO: Descomentar cuando el backend estÃ© funcionando correctamente
          /*
          const response = await authService.getMe();
          console.log('âœ… Token vÃ¡lido, usuario autenticado:', response.data.email);
          */
          
          dispatch({
            type: 'LOGIN_SUCCESS',
            payload: {
              token,
              user: user
            }
          });
        } catch (error) {
          console.error('âŒ Error al parsear usuario, limpiando storage:', error);
          localStorage.removeItem('authToken');
          localStorage.removeItem('user');
          dispatch({ type: 'LOGOUT' });
        }
      } else {
        console.log('ðŸ”“ No hay token, usuario no autenticado');
        dispatch({ type: 'LOGOUT' });
      }
    };

    initializeAuth();
  }, []);

  // FunciÃ³n de login
  const login = async (credentials) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      console.log('ðŸ”„ AuthContext: Iniciando login...');
      const response = await authService.login(credentials);
      console.log('âœ… AuthContext: Respuesta del servidor:', response);
      
      // Guardar en localStorage
      localStorage.setItem('authToken', response.token);
      localStorage.setItem('user', JSON.stringify(response.user));
      
      dispatch({
        type: 'LOGIN_SUCCESS',
        payload: response
      });
      
      console.log('âœ… AuthContext: Estado actualizado, usuario autenticado');
      return response;
    } catch (error) {
      console.error('âŒ AuthContext: Error en login:', error);
      const errorMessage = error.response?.data?.error || 'Error al iniciar sesiÃ³n';
      dispatch({
        type: 'LOGIN_FAILURE',
        payload: errorMessage
      });
      throw error;
    }
  };

  // FunciÃ³n de logout
  const logout = () => {
    localStorage.removeItem('authToken');
    localStorage.removeItem('user');
    dispatch({ type: 'LOGOUT' });
  };

  // FunciÃ³n de registro
  const register = async (userData) => {
    dispatch({ type: 'LOGIN_START' });
    
    try {
      const response = await authService.register(userData);
      
      // Guardar en localStorage
      localStorage.setItem('authToken', response.token);
      localStorage.setItem('user', JSON.stringify(response.user));
      
      dispatch({
        type: 'LOGIN_SUCCESS',
        payload: response
      });
      
      return response;
    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Error al registrar usuario';
      dispatch({
        type: 'LOGIN_FAILURE',
        payload: errorMessage
      });
      throw error;
    }
  };

  // FunciÃ³n para actualizar el perfil del usuario
  const updateUser = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
    dispatch({
      type: 'UPDATE_USER',
      payload: userData
    });
  };

  // FunciÃ³n para cambiar contraseÃ±a
  const changePassword = async (passwordData) => {
    try {
      const response = await authService.changePassword(passwordData);
      return response;
    } catch (error) {
      throw error;
    }
  };

  // FunciÃ³n para actualizar perfil
  const updateProfile = async (profileData) => {
    try {
      const response = await authService.updateProfile(profileData);
      updateUser(response.user);
      return response;
    } catch (error) {
      throw error;
    }
  };

  // Verificar permisos por rol
  const hasPermission = (requiredRoles) => {
    if (!state.user || !Array.isArray(requiredRoles)) return false;
    return requiredRoles.includes(state.user.role);
  };

  // Verificar si es admin
  const isAdmin = () => {
    return state.user?.role === 'admin';
  };

  // Verificar si es coordinador o admin
  const isCoordinator = () => {
    return ['admin', 'coordinador'].includes(state.user?.role);
  };

  const value = {
    ...state,
    login,
    logout,
    register,
    updateUser,
    changePassword,
    updateProfile,
    hasPermission,
    isAdmin,
    isCoordinator
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};

// Hook para usar el contexto
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth debe usarse dentro de AuthProvider');
  }
  return context;
};

export default AuthContext;
